mapper.update:
UPDATE J_참여자관리_지점
SET
    유형1=#{memberType1},
    유형2=#{memberType2}
WHERE 지점 = #{memberBranch}

UPDATE J_참여자관리_로그인정보
SET
    비밀번호 = #{memberUserPW}
WHERE
    아이디 = #{memberUserID}

UPDATE J_참여자관리_로그인정보 SET
비밀번호 = #{memberUserChangePW},
전화번호 = #{memberPhoneNumber}, 금일일반취업 = #{memberTodayEmployment}, 금일알선취업 = #{memberTodayPlacement}, 금주일반취업 = #{memberToWeekEmployment}, 금주알선취업 = #{memberToWeekPlacement}, 금월일반취업 = #{memberToMonthEmployment}, 금월알선취업 = #{memberToMonthPlacement}, 금년일반취업 = #{memberToYearEmployment}, 금년알선취업 = #{memberToYearPlacement}, 실적저장일 = GETDATE() WHERE 아이디 = #{memberUserID}
